import { motion } from "framer-motion";

export default function ResumeSection() {
  const experiences = [
    {
      title: "Technical Internship",
      company: "Tasmim Web",
      date: "April 2023 – June 2023",
      details: [
        "Developed an online table reservation web application for partner restaurants.",
        "Built using HTML, CSS, JavaScript, PHP (Laravel), and MySQL.",
      ],
    },
    {
      title: "Introductory Internship",
      company: "Kompara Cash",
      date: "July 2022 – August 2022",
      details: [
        "Contributed to creating a comparison tool for international money transfer companies.",
      ],
    },
  ];

  const education = [
    {
      degree: "Software Engineering Student",
      school: "1337 Coding School, Khouribga",
      date: "2023 – Present",
    },
    {
      degree: "DUT in Computer Engineering",
      school: "Sultan Moulay Slimane University, Fquih Ben Salah",
      date: "2021 – 2023",
    },
  ];

  return (
    <section
      id="resume"
      className="relative min-h-screen py-28 px-8 bg-[#0b0f0d] text-white overflow-hidden"
    >
      {/* Ben 10 green aura background */}
      <div className="absolute inset-0 -z-10 opacity-40 blur-3xl bg-[radial-gradient(circle_at_center,rgba(34,197,94,0.2),transparent_70%)]"></div>

      <div className="max-w-6xl mx-auto">
        {/* Title */}
        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-5xl font-extrabold mb-20 text-center text-green-400 drop-shadow-[0_0_20px_#00ff99]"
        >
          Resume
        </motion.h2>

        {/* Two columns */}
        <div className="grid md:grid-cols-2 gap-16">
          {/* Experience */}
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.7 }}
          >
            <h3 className="text-3xl font-semibold text-green-400 mb-6 border-b border-green-500/30 pb-2">
              Experience
            </h3>
            <div className="space-y-8">
              {experiences.map((exp, idx) => (
                <motion.div
                  key={idx}
                  className="bg-[#101511] rounded-2xl p-6 border border-green-500/20 hover:border-green-500/60 hover:shadow-[0_0_25px_rgba(0,255,100,0.25)] transition-all duration-300"
                  whileHover={{ scale: 1.02 }}
                >
                  <h4 className="text-xl font-bold mb-1">{exp.title}</h4>
                  <p className="text-green-300 text-sm mb-1">{exp.company}</p>
                  <p className="text-gray-400 text-xs mb-3">{exp.date}</p>
                  <ul className="list-disc list-inside text-gray-300 text-sm space-y-1">
                    {exp.details.map((d, i) => (
                      <li key={i}>{d}</li>
                    ))}
                  </ul>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Education */}
          <motion.div
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.7 }}
          >
            <h3 className="text-3xl font-semibold text-green-400 mb-6 border-b border-green-500/30 pb-2">
              Education
            </h3>
            <div className="space-y-8">
              {education.map((edu, idx) => (
                <motion.div
                  key={idx}
                  className="bg-[#101511] rounded-2xl p-6 border border-green-500/20 hover:border-green-500/60 hover:shadow-[0_0_25px_rgba(0,255,100,0.25)] transition-all duration-300"
                  whileHover={{ scale: 1.02 }}
                >
                  <h4 className="text-xl font-bold mb-1">{edu.degree}</h4>
                  <p className="text-green-300 text-sm mb-1">{edu.school}</p>
                  <p className="text-gray-400 text-xs">{edu.date}</p>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
}
