import { motion } from "framer-motion";

function Skills() {
  const skills = ["React", "JavaScript", "Tailwind CSS", "HTML", "CSS", "Framer Motion"];

  return (
    <section id="skills" className="py-20 bg-gray-900 px-4">
      <h2 className="text-4xl font-bold text-omnitrixGreen text-center mb-12">Skills</h2>
      <div className="flex flex-wrap justify-center gap-4">
        {skills.map((skill, i) => (
          <motion.span
            key={i}
            whileHover={{ scale: 1.2, boxShadow: "0 0 10px #00FF00" }}
            className="px-4 py-2 bg-gray-800 text-gray-200 rounded shadow-md cursor-pointer transition"
          >
            {skill}
          </motion.span>
        ))}
      </div>
    </section>
  );
}

export default Skills;
