import ProjectCard from "./ProjectCard";

// src/components/Projects.jsx
import { motion } from "framer-motion";

const projects = [
  { title: "Inception", img: "/assets/INCEPTION.png" },
  { title: "Tic Tac Toe", img: "/assets/projects/project2.png" },
  { title: "E-commerce UI", img: "/assets/projects/project3.png" },
];

export default function Projects() {
  return (
    <section id="projects" className="py-20 bg-black text-white relative overflow-hidden">
      <h2 className="text-4xl font-bold text-center mb-12 text-green-400 drop-shadow-[0_0_15px_#00ff99]">
        ðŸš€ Projects
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10 px-6 md:px-20">
        {projects.map((project, i) => (
          <motion.div
            key={i}
            className="relative rounded-2xl overflow-hidden shadow-xl border border-green-500/40 group"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: i * 0.1 }}
          >
            <img
              src={project.img}
              alt={project.title}
              className="w-full h-60 object-cover group-hover:scale-110 transition-transform duration-500"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent opacity-0 group-hover:opacity-100 transition duration-500 flex items-end p-4">
              <h3 className="text-lg font-semibold">{project.title}</h3>
            </div>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
